import {
  isNum,
  isFn,
  isNode,
  isNodeList,
  isObject,
  isString,
  isArray,
  isSvg,
  noop
} from './is'

const isProduction = process.env.NODE_ENV === 'production'

/**
 * isEffect
 *
 * @param {mixed} condition  触发条件
 * @param {string} message 触发提示
 * @param {string} [type='error'] 触发类型
 * @returns
 */
function isEffect(
  condition,
  message,
  type = 'error' // log | error | warn
) {
  if (!isProduction) {
    // condition passed: do not log
    if (condition) {
      return
    }

    // Condition not passed
    const text = `${type}: ${message}`

    try {
      console[type.toLowerCase()](text)
    } catch (x) {
      console.log(x)
    }
  }
}

export {
  isEffect as default,
  isNum,
  isFn,
  isNode,
  isNodeList,
  isObject,
  isString,
  isArray,
  isSvg,
  noop
}
